func getFloorFromInput(_ input: String) -> Int {

// 	Split string in array, by each "key",
//	and difference in array will be the floor
	let up = input.components(separatedBy: "(")
	let down = input.components(separatedBy: ")")
	return up.count - down.count
}


func firstBasementMomentFromInput(_ input: String) -> Int {

	var floor: Int = 0
	let chars = Array(input)
	var index = 0

	repeat {
		switch chars[index] {
			case "(":
			floor += 1;
			default:
			floor -= 1;
		}
		index += 1
	} while floor != -1 && index < chars.count

	return index
}
